import React, { useState, useEffect, useContext } from "react";
import style from  './AllBoards.module.css';
import Axios from 'axios';
import { Link } from "react-router-dom";
import Loader from "../../../common/Loader/Loader";
import { AuthContext } from '../../../context/Auth';
import Sidebar from "../../../components/Backend/Sidebar/Sidebar";

export const AllGroups = () => {

    const { currentUser } = useContext(AuthContext);

    const [groupContents, setGroupContents] = useState({});
    const [showGroup, setShowGroup] = useState(false);
    const [loading, setLoading] = useState(true);

    const userId = currentUser.uid;

    document.title = "Pro Organizer";

    useEffect(() => {
        getGroupContents();
    }, [showGroup]);

    // const getGroupContents = () => {
    //     Axios
    //     .get(`https://kido-bookt-backend-default-rtdb.firebaseio.com/groupContents.json`)
    //     .then((response) => {
    //       setTimeout(setGroupContents(response.data), 50000);
    //       setLoading(false);
    //       if (groupContents !== null) {
    //         setShowGroup(true);
    //       } else {setShowGroup(false);}
    //     })
    //     .catch((error) => console.log(error));
    // };

    const getGroupContents = () => {
      Axios
      .get(`https://kido-bookt-backend-default-rtdb.firebaseio.com/groupContents.json`)
      .then((response) => {
        setTimeout(setGroupContents(response.data), 50000);
        setLoading(false);
        if (groupContents !== null) {
          setShowGroup(false);
        } else setShowGroup(true);
      })
      .catch((error) => console.log(error));
  };


    return (
        <div>
          <br />
          {loading ? (
            <Loader></Loader>
          ) : (
            <>
               <div className="wrapper d-flex align-items-stretch">
              <Sidebar />
              <div className={style.BoardContainer}>
                <div className={style.BoardsTitle}>
                    <span>Boards</span>
                </div>
                <br />
                {showGroup ? (
                  <div className={style.BoardsData}>
                    {groupContents &&
                      Object.entries(groupContents).map((item) => (
                        <Link
                          to={{
                            pathname: "/group/" + item[1].ageGroup,
                            state: {
                              teachersName: item[1].teachersName,
                              groupId: item[0],
                            },
                          }}
                        >
                          <div className={style.BoardItem} key={item[1].ageGroup}>
                            <span className={style.BoardName}>
                               {item[1].ageGroup}
                            </span>
                          </div>
                        </Link>
                      ))}
                  </div>
                ) : (
                  <p>
                    You haven't created any Group. Kindly click on the 'Create
                    a Group' button in the Sidebar bar to create a Group.
                  </p>
                )}
              </div>
              </div>
            </>
           )} 
        </div>
      );
    }


export default AllGroups;
