import React, { Component } from 'react';

class ChildDetail extends Component {
    constructor(props) {
        super(props);
        // create a ref to store the DOM element
        this.nameEl = React.createRef();
        this.handleSubmit = this.handleSubmit.bind(this);
      }

      handleSubmit(e) {
        e.preventDefault();
        // alert(this.nameEl.current.value);
      }

    render() {
        return (

        <div className="card col-lg-12">
            <div className="card-body">
            <h1 className="py-3">Let’s get to know your child</h1>
                <div className="form-row">
                    <div className="form-group col-md-6">
                        <label htmlFor="childfname">First Name</label>
                        <input type="text" className="form-control" id="childfname" name="childfname" 
                         ref={this.nameEl} 
                        value={this.props.childfname} 
                        onChange={this.props.onChange} 
                        placeholder="Enter Your First Name"/>
                        <span className="text-danger"><small>{this.props.childfnameError}</small></span>
                    </div>
                    <div className="form-group col-md-6">
                        <label htmlFor="childlname">Last Name</label>
                        <input type="text" className="form-control" id="childlname" name="childlname" 
                        value={this.props.childlname} 
                        onChange={this.props.onChange} 
                        placeholder="Enter Your Last Name"/>
                        <span className="text-danger"><small>{this.props.childlnameError}</small></span>
                    </div>
                    <button class="btn btn-primary mb-4" onClick={this.handleSubmit} type="button" data-toggle="collapse" data-target="#collapseExample" aria-expanded="false" aria-controls="collapseExample">
                        Submit
                    </button>
                </div>

                <div class="collapse" id="collapseExample">
                    <h2>How old is <span>{this.nameEl.current.value}</span></h2>
                    <div className="form-row">
                        <div className="form-group col-md-6">
                            <label htmlFor="phone">Phone Number</label>
                            <input type="number" className="form-control" id="phone" name="phone" 
                            value={this.props.phone} 
                            onChange={this.props.onChange} 
                            placeholder="Enter Your Phone Number"/>
                            <span className="text-danger"><small>{this.props.phoneError}</small></span>
                        </div>
                        <div className="form-group col-md-6">
                            <label htmlFor="email">Email Address</label>
                            <input type="email" className="form-control" id="email" name="email" 
                            value={this.props.email} 
                            onChange={this.props.onChange} 
                            placeholder="Enter Your Email Address"/>
                            <span className="text-danger"><small>{this.props.emailError}</small></span>
                        </div>
                    </div>
                    <button type="submit" onClick={this.props.next} className="btn btn-primary">Submit</button>
                </div>

            </div>
        </div>


            // <div className="form-card">
            //     <div className="form-group">
            //         <label>Password</label>
            //         <span className="pull-right text-danger"><small>{this.props.passwordError}</small></span>
            //         <input className="form-control"
            //                type="password"
            //                id="password"
            //                value={this.props.password}
            //                onChange={this.props.onChange}/>
            //     </div>

            //     <div className="form-group">
            //         <label>Confirm Password</label>
            //         <span className="pull-right text-danger"><small>{this.props.confirmError}</small></span>
            //         <input className="form-control"
            //                type="password"
            //                id="confirm"
            //                value={this.props.confirm}
            //                onChange={this.props.onChange}/>
            //     </div>

            //     <div className="form-group">
            //         <button className="btn btn-danger"
            //                 onClick={this.props.prev}>Prev</button>
            //         <button className="btn btn-success"
            //                 onClick={this.props.next}>Next</button>
            //     </div>
            // </div>
        )
    }
}

export default ChildDetail;
