import React, { Component } from 'react';
import child1 from '../assets/child-icon1.png';
import child2 from '../assets/child-icon2.png';
import child3 from '../assets/child-icon3.png';
import child4 from '../assets/child-icon4.png';
import child5 from '../assets/child-icon5.png';

class ChildDetail extends Component {
    constructor(props) {
        super(props);
        this.state = {  
        value: "",
        childfname: ""
        }
      }

      handleChange(e){
        this.setState({value: e.target.value})
      }

      handleClick(){
        this.setState({childfname: this.state.value})
      }

    render() {
        return (

        <div className="card col-lg-12">
            <div className="card-body">
            <h1 className="py-3">Let’s get to know your child</h1>
                <div className="form-row">
                    <div className="form-group col-md-6">
                        <label htmlFor="childfname">First Name</label>
                        <input type="text" className="form-control" id="childfname" name="childfname" 
                         ref={this.nameEl} 
                        value={this.props.childfname , this.state.value}
                        onChange={this.props.onChange, this.handleChange.bind(this)} 
                        placeholder="Enter Your First Name"/>
                        <span className="text-danger"><small>{this.props.childfnameError}</small></span>
                    </div>
                    <div className="form-group col-md-6">
                        <label htmlFor="childlname">Last Name</label>
                        <input type="text" className="form-control" id="childlname" name="childlname" 
                        value={this.props.childlname} 
                        onChange={this.props.onChange} 
                        placeholder="Enter Your Last Name"/>
                        <span className="text-danger"><small>{this.props.childlnameError}</small></span>
                    </div>
                    <button class="btn btn-primary mb-4" onClick={this.handleClick.bind(this)} type="button" data-toggle="collapse" data-target="#collapseExample" aria-expanded="false" aria-controls="collapseExample">
                        Next
                    </button>
                </div>

                <div class="collapse" id="collapseExample">
                    <h2>How old is <span>{this.state.childfname}</span></h2>
                    <div className="form-row">
                        <div className="form-group col-md-2 text-center">
                            <img src={child1} class="img-responsive" alt="Image"/>
                            <div class="form-check">
                                <input class="form-check-input" type="radio" name="exampleRadios" id="child1" value="child1" data-toggle="collapse" data-target="#collapseExample1" aria-expanded="false" aria-controls="collapseExample1" />
                                <label class="form-check-label" for="child1">
                                        15 mth to 2 yrs
                                </label>
                            </div>
                        </div>
                        <div className="form-group col-md-2 text-center">
                            <img src={child2} class="img-responsive" alt="Image"/>
                             <div class="form-check">
                                <input class="form-check-input" type="radio" name="exampleRadios" id="child2" value="child2" data-toggle="collapse" data-target="#collapseExample1" aria-expanded="false" aria-controls="collapseExample1"/>
                                <label class="form-check-label" for="child2">
                                    2 yrs to 3 yrs
                                </label>
                            </div>
                        </div>
                        <div className="form-group col-md-2 text-center">
                            <img src={child3} class="img-responsive" alt="Image"/>
                             <div class="form-check">
                                <input class="form-check-input" type="radio" name="exampleRadios" id="child3" value="child3" data-toggle="collapse" data-target="#collapseExample1" aria-expanded="false" aria-controls="collapseExample1"/>
                                <label class="form-check-label" for="child3">
                                    3 yrs to 4 yrs
                                </label>
                            </div>
                        </div>
                        <div className="form-group pl-4 col-md-3 text-center">
                            <img src={child4} class="img-responsive" alt="Image"/>
                             <div class="form-check">
                                <input class="form-check-input" type="radio" name="exampleRadios" id="child4" value="child4" data-toggle="collapse" data-target="#collapseExample1" aria-expanded="false" aria-controls="collapseExample1"/>
                                <label class="form-check-label" for="child4">
                                    4 yrs to 5 yrs
                                </label>
                            </div>
                        </div>
                        <div className="form-group col-md-3 text-center">
                           <img src={child5} class="img-responsive" alt="Image"/>
                            <div class="form-check">
                                <input class="form-check-input" type="radio" name="exampleRadios" id="child5" value="child5" data-toggle="collapse" data-target="#collapseExample1" aria-expanded="false" aria-controls="collapseExample1"/>
                                <label class="form-check-label" for="child5">
                                    5 yrs to 6 yrs
                                </label>
                            </div>
                        </div>
                    </div>
                </div>


                <div class="collapse" id="collapseExample1">
                <h2>When is <span>{this.state.childfname}</span>’s birthday</h2>
                <div className="form-row">
                    <div className="form-group col-md-8">
                    <input type="date" id="datepicker" className="form-control" />
                    </div>
                </div>
                <button type="submit" onClick={this.props.next} className="btn btn-primary">Submit</button>
                </div>

            </div>
        </div>


            // <div className="form-card">
            //     <div className="form-group">
            //         <label>Password</label>
            //         <span className="pull-right text-danger"><small>{this.props.passwordError}</small></span>
            //         <input className="form-control"
            //                type="password"
            //                id="password"
            //                value={this.props.password}
            //                onChange={this.props.onChange}/>
            //     </div>

            //     <div className="form-group">
            //         <label>Confirm Password</label>
            //         <span className="pull-right text-danger"><small>{this.props.confirmError}</small></span>
            //         <input className="form-control"
            //                type="password"
            //                id="confirm"
            //                value={this.props.confirm}
            //                onChange={this.props.onChange}/>
            //     </div>

            //     <div className="form-group">
            //         <button className="btn btn-danger"
            //                 onClick={this.props.prev}>Prev</button>
            //         <button className="btn btn-success"
            //                 onClick={this.props.next}>Next</button>
            //     </div>
            // </div>
        )
    }
}

export default ChildDetail;
