import React, { Component } from 'react';
import Axios from 'axios';

class Schedule extends Component {

    constructor(props) {
        super(props);

        this.state = {
            selectedChildage: '',
            ageGroups: {},
        }
      }

        // Get Data from LocalStorage ..
        componentDidMount() {
            this.childData = JSON.parse(localStorage.getItem('childdetails'));

            if (localStorage.getItem('childdetails')) {
                this.setState({
                    selectedChildage: this.childData.selectedChildage
                })
            } else {
                this.setState({
                    selectedChildage: ''
                })
            }


            Axios.get('https://kido-book-trial-default-rtdb.firebaseio.com/ageGroups.json')
            .then(response => {
                console.log(response);
                this.setState({
                    ageGroups: response.data
                })
            })
                .catch(error => {console.log(error)});

        }


    render() {
        return (

            Object.entries(this.state.ageGroups).map((item) => {
            <section className="kids-schedule py-5">
            <div className="container">
                <div className="row justify-content-center">
                    <div className="col-lg-8">
                    <div className="scheduler border bg-white p-4">
                        <h5>Schedule a Trial className for Age Group - <span className="childage-text">{this.state.selectedChildage}</span></h5>
                        <p>Select a Date</p>
                        <div className="available-dates pb-4">
                            <div className="dynamic-dates" data-slick-index="0" aria-hidden="false">
                                <input type="radio" id="dateselect1" name="schedule_date"  className="inputdates" value="73" data-val="Mon 21 Dec" data-day="Monday" data-date="21st" data-month="December"/>
                                <label className="" htmlFor="dateselect1">
                                    <div className="sdday">Mon</div>
                                    <div className="sddate">21</div>
                                    <div className="sdmonth">Dec</div>
                                </label>
                            </div>
                            <div className="dynamic-dates" data-slick-index="1" aria-hidden="false">
                                <input type="radio" id="dateselect2" name="schedule_date" className="inputdates" value="85" data-val="Mon 04 Jan" data-day="Monday" data-date="04th" data-month="January"/>
                                <label className="" htmlFor="dateselect2">
                                    <div className="sdday">Mon</div>
                                    <div className="sddate">04</div>
                                    <div className="sdmonth">Jan</div>
                                </label>
                            </div>
                            <div className="dynamic-dates" data-slick-index="2" aria-hidden="false">
                                <input type="radio" id="dateselect3" name="schedule_date" className="inputdates" value="86" data-val="Mon 11 Jan" data-day="Monday" data-date="11th" data-month="January"/>
                                <label className="" htmlFor="dateselect3">
                                    <div className="sdday">Mon</div>
                                    <div className="sddate">11</div>
                                    <div className="sdmonth">Jan</div>
                                </label>
                            </div>
                            <div className="dynamic-dates" data-slick-index="3" aria-hidden="false">
                                <input type="radio" id="dateselect4" name="schedule_date" className="inputdates" value="87" data-val="Mon 18 Jan" data-day="Monday" data-date="18th" data-month="January"/>
                                <label className="" htmlFor="dateselect4">
                                    <div className="sdday">Mon</div>
                                    <div className="sddate">18</div>
                                    <div className="sdmonth">Jan</div>
                                </label>
                            </div>
                        </div>

                        <p>Select a Time For <span className="childage-text"><b>Mon 11 Jan</b></span></p>
                        <div className="timeslotselect">			
                            <div className="timeslotdiv">
                                <input type="radio" required="" className="timeslotinput" name="schedule_time" id="timdateslot1" value="10:30" data-time="10:30 AM"/>
                                <label htmlFor="timdateslot1">10:30 AM</label>
                            </div>
                            {/* <div className="timeslotdiv">
                                <input type="radio" required="" className="timeslotinput" name="schedule_time" id="timdateslot2" value="16:30" data-time="04:30 PM"/>
                                <label htmlFor="timdateslot2">04:30 PM</label>
                            </div> */}
                        </div>

                        <div className="selecteddate mt-3">
							<div className="select-day"><small>Monday</small></div>
							<div className="select-date"><small>11th</small></div>
							<div className="select-month"><small>January</small></div>
							<div className="select-time"><small>04:30 PM</small></div>
						</div>

                        <div className="confirmstaus">
							<p>Due to high demand, itâ€™s difficult to reschedule a trial className. <br/>Please pick your slot carefully.</p>
						</div>

                        <div className="form-group">
							<button className="btn button-confirm-details trialBtn" type="submit" name="submit">
								Confirm Trial className
							</button>
						</div>

                    </div>
                    </div>
                </div>
            </div>
            </section>
            })
        )
    }
}

export default Schedule;
