import React, { Component } from 'react';
import ChildDetail from './ChildDetail';
import virtualschool from '../assets/virtual-pre-school.png';
import smartschool from '../assets/smart-school.png';

class LearningOptions extends Component {

    render() {
        return (

            <div className="step3">
            <div className="card col-lg-12">
                <div className="card-body">
                <h1 className="py-3">Choose the best learning option for allen <span>{this.props.childfname}</span></h1>
                <div className="form-row">
                        <div className="form-group col-md-12">
                            <div className="border shadow py-4 px-5">
                                <input className="form-check-input" type="radio" name="school" id="virtualpreschool" 
                                        value={this.props.virtualpreschool} 
                                        onChange={this.props.onChange}  
                                />
                                <div className="d-flex justify-content-between">
                                <span className="text-success"><i class="fas fa-chalkboard-teacher fa-2x"></i></span>
                                <h4>The Virtual Preschool</h4>
                                <i>- Online learning</i>
                                <span className="text-info"><i class="fas fa-info-circle fa-1x"></i></span>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="form-row">
                        <div className="form-group col-md-12">
                            <div className="border shadow py-4 px-5">
                                <input className="form-check-input" type="radio" name="school" id="virtualpreschool" 
                                        value={this.props.virtualpreschool} 
                                        onChange={this.props.onChange}  
                                />
                                <div className="d-flex justify-content-between">
                                <span className="text-warning"><i class="fas fa-school fa-2x"></i></span>
                                <h4>Smart Kit Program</h4>
                                <i>- Parent driven teaching</i>
                                <span className="text-info"  data-toggle="modal" data-target="#exampleModal"><i class="fas fa-info-circle fa-1x"></i></span>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="d-flex justify-content-between">
                    <button  type="submit" className="btn btn-danger" onClick={this.props.prev}>Back</button>
                    <button type="submit" onClick={this.props.next} className="btn btn-primary">Submit</button>
                    </div>
                </div>
            </div>
            {/* // <!-- Modal --> */}
<div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        ...
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
        <button type="button" class="btn btn-primary">Save changes</button>
      </div>
    </div>
  </div>
</div>

        </div>



